var warning = confirm("You are submitting the request for a verification \nPlease wait until your request is processed."); 

if (warning == true) 
{ 
var namespace_prefix='Verification'; 
var gettingnamespace=sforce.connection.query("SELECT NamespacePrefix FROM ApexClass where Name = 'Verification' limit 1"); 

var getname=gettingnamespace.getArray("records"); 

if(getname.length>0) 
{ 
namespace_prefix=getname[0].NamespacePrefix; 
if(namespace_prefix!=null && namespace_prefix!='') 
{ 
namespace_prefix=namespace_prefix+'.'+'Verification'; 

} 
else 
{ 
namespace_prefix='Verification'; 
} 

} 
var result=sforce.apex.execute(namespace_prefix,"VerifyBenifits", 
{ 
RecordId:"{!Lead.Id}" 
} 
); 

} 
window.location.reload();
